import React, { useEffect, useState } from 'react';
import axios from 'axios';

function BookingList() {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchBookings();
    }, []);

    const fetchBookings = () => {
        axios.get('/api/bookings')
            .then(res => setBookings(res.data.bookings))
            .catch(() => setError('Error fetching bookings'));
    };

    const handleCancel = async (bookingId) => {
        try {
            await axios.delete(`/api/bookings/${bookingId}`);
            fetchBookings(); // Refresh list
        } catch {
            setError('Error cancelling booking');
        }
    };

    return (
        <div>
            <h2>Bookings & Participants</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {bookings.map(booking => (
                    <li key={booking._id}>
                        Event: {booking.eventName || booking.eventId} | Tickets: {booking.ticketCount} | Name: {booking.participantName}
                        <button onClick={() => handleCancel(booking._id)} style={{ marginLeft: 10 }}>Cancel</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default BookingList;
