import React, { useEffect, useState } from 'react';
import axios from 'axios';

function EventList() {
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');
    const [editingId, setEditingId] = useState(null);
    const [editName, setEditName] = useState('');
    const [editDate, setEditDate] = useState('');

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = () => {
        axios.get('/api/events')
            .then(res => setEvents(res.data.events))
            .catch(() => setError('Error fetching events'));
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/events/${id}`);
            fetchEvents();
        } catch {
            setError('Error deleting event');
        }
    };

    const startEdit = (event) => {
        setEditingId(event._id);
        setEditName(event.name);
        setEditDate(event.date);
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`/api/events/${editingId}`, { name: editName, date: editDate });
            setEditingId(null);
            setEditName('');
            setEditDate('');
            fetchEvents();
        } catch {
            setError('Error updating event');
        }
    };

    return (
        <div>
            <h2>Events</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {events.map(event => (
                    <li key={event._id}>
                        {editingId === event._id ? (
                            <form onSubmit={handleUpdate} style={{ display: 'inline' }}>
                                <input
                                    type="text"
                                    value={editName}
                                    onChange={e => setEditName(e.target.value)}
                                    required
                                />
                                <input
                                    type="date"
                                    value={editDate}
                                    onChange={e => setEditDate(e.target.value)}
                                    required
                                />
                                <button type="submit">Save</button>
                                <button type="button" onClick={() => setEditingId(null)}>Cancel</button>
                            </form>
                        ) : (
                            <>
                                <strong>{event.name}</strong> - {event.date}
                                <button onClick={() => startEdit(event)} style={{ marginLeft: 10 }}>Edit</button>
                                <button onClick={() => handleDelete(event._id)} style={{ marginLeft: 10 }}>Delete</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default EventList;
