import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventForm = ({ eventId, onEventSaved }) => {
    const [eventData, setEventData] = useState({
        title: '',
        date: '',
        location: '',
        description: ''
    });

    useEffect(() => {
        if (eventId) {
            axios.get(`/api/events/${eventId}`)
                .then(response => {
                    setEventData(response.data);
                })
                .catch(error => {
                    console.error('Error fetching event data:', error);
                });
        }
    }, [eventId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEventData({ ...eventData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const request = eventId ? 
            axios.put(`/api/events/${eventId}`, eventData) : 
            axios.post('/api/events', eventData);

        request
            .then(response => {
                onEventSaved(response.data);
                setEventData({ title: '', date: '', location: '', description: '' });
            })
            .catch(error => {
                console.error('Error saving event:', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="title" 
                value={eventData.title} 
                onChange={handleChange} 
                placeholder="Event Title" 
                required 
            />
            <input 
                type="date" 
                name="date" 
                value={eventData.date} 
                onChange={handleChange} 
                required 
            />
            <input 
                type="text" 
                name="location" 
                value={eventData.location} 
                onChange={handleChange} 
                placeholder="Location" 
                required 
            />
            <textarea 
                name="description" 
                value={eventData.description} 
                onChange={handleChange} 
                placeholder="Description" 
                required 
            />
            <button type="submit">{eventId ? 'Update Event' : 'Create Event'}</button>
        </form>
    );
};

export default EventForm;
