import React, { useState } from 'react';
import axios from 'axios';

function EventForm({ eventId, onEventSaved }) {
    const [name, setName] = useState('');
    const [date, setDate] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (eventId) {
                await axios.put(`/api/events/${eventId}`, { name, date });
            } else {
                await axios.post('/api/events', { name, date });
            }
            setName('');
            setDate('');
            setError('');
            if (onEventSaved) onEventSaved();
        } catch (err) {
            setError('Error saving event');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{eventId ? 'Update Event' : 'Create Event'}</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>Name:</label>
                <input
                    type="text"
                    value={name}
                    onChange={e => setName(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Date:</label>
                <input
                    type="date"
                    value={date}
                    onChange={e => setDate(e.target.value)}
                    required
                />
            </div>
            <button type="submit">{eventId ? 'Update' : 'Create'}</button>
        </form>
    );
}

export default EventForm;
